sqlMap.select:
Select 手术次数 From ( select ' ' as 编码, '全院' as 名称, sum(decode(B.序号, 1, 1, 0)) as 病人数, count(B.手术操作ID) as 手术次数, sum(decode(B.切口 || B.愈合, 'Ⅰ甲', 1, 0)) as 甲1, sum(decode(B.切口 || B.愈合, 'Ⅰ乙', 1, 0)) as 乙1, sum(decode(B.切口 || B.愈合, 'Ⅰ丙', 1, 0)) as 丙1, sum(decode(B.切口 || B.愈合, 'Ⅱ甲', 1, 0)) as 甲2, sum(decode(B.切口 || B.愈合, 'Ⅱ乙', 1, 0)) as 乙2, sum(decode(B.切口 || B.愈合, 'Ⅱ丙', 1, 0)) as 丙2, sum(decode(B.切口 || B.愈合, 'Ⅲ甲', 1, 0)) as 甲3, sum(decode(B.切口 || B.愈合, 'Ⅲ乙', 1, 0)) as 乙3, sum(decode(B.切口 || B.愈合, 'Ⅲ丙', 1, 0)) as 丙3, sum(decode(B.切口 || B.愈合, null, 1, '', 1, 0)) as 无切口数, sum(decode(B.麻醉类型, '全麻', 1, 0)) as 全麻, sum(decode(B.麻醉类型, '局麻', 1, 0)) as 局麻, sum(decode(B.麻醉类型, '持硬', 1, 0)) as 持硬, sum(decode(B.麻醉类型, '其他', 1, 0)) as 其他, sum(B.手术日期 - trunc(A.入院日期)) as 术前日, sum(decode(B.序号, 1, A.费用和, 0)) as 费用 from 病案主页 A, ( select Row_Number() Over(Partition by a.病人id,a.主页id Order by 手术日期) as 序号,a.* from 病人手麻记录 a where a.记录来源=4 order by a.病人id,a.主页id,序号) B
where A.病人ID = B.病人ID and A.主页ID = B.主页ID and A.出院日期 >= TO_DATE(#StartTime#,'YYYY-MM-DD HH24:MI:SS') and A.出院日期 < TO_DATE(#EndTime#,'YYYY-MM-DD HH24:MI:SS') + 1
)

Select count(A.主页ID) as total, nvl(sum(decode(B.出院情况,'好转',1,0)), 0) as data, null as name from (select 病人ID,主页ID,入院日期,出院日期,住院天数,出院科室ID as 科室ID,抢救次数,成功次数,是否确诊 from 病案主页
where 出院日期 >= To_Date(#StartTime#, 'YYYY-MM-DD') and 出院日期 < To_Date(#EndTime#, 'YYYY-MM-DD')+1
) A,病人诊断记录 B,诊断符合情况 C Where A.主页ID = B.主页ID and A.病人ID=B.病人ID and B.诊断次序=1 and B.诊断类型=3 and B.记录来源=4 and B.编码序号=1 and A.主页ID = C.主页ID and A.病人ID=C.病人ID and C.符合类型=2

Select count(A.主页ID) as total, nvl(sum(decode(B.出院情况,'治愈',1,0)), 0) as data, null as name from (select 病人ID,主页ID,入院日期,出院日期,住院天数,出院科室ID as 科室ID,抢救次数,成功次数,是否确诊 from 病案主页
where 出院日期 >= To_Date(#StartTime#, 'YYYY-MM-DD') and 出院日期 < To_Date(#EndTime#, 'YYYY-MM-DD')+1
) A,病人诊断记录 B,诊断符合情况 C Where A.主页ID = B.主页ID and A.病人ID=B.病人ID and B.诊断次序=1 and B.诊断类型=3 and B.记录来源=4 and B.编码序号=1 and A.主页ID = C.主页ID and A.病人ID=C.病人ID and C.符合类型=2

Select count(A.主页ID) as total, nvl(sum(decode(B.出院情况,'死亡',1,0)), 0) as data, null as name from (select 病人ID,主页ID,入院日期,出院日期,住院天数,出院科室ID as 科室ID,抢救次数,成功次数,是否确诊 from 病案主页
where 出院日期 >= To_Date(#StartTime#, 'YYYY-MM-DD') and 出院日期 < To_Date(#EndTime#, 'YYYY-MM-DD')+1
) A,病人诊断记录 B,诊断符合情况 C Where A.主页ID = B.主页ID and A.病人ID=B.病人ID and B.诊断次序=1 and B.诊断类型=3 and B.记录来源=4 and B.编码序号=1 and A.主页ID = C.主页ID and A.病人ID=C.病人ID and C.符合类型=2
